mapper.select:
select * from lesson
where 1=1

  and title=#{title,jdbcType=VARCHAR}


  and cid=#{cid,jdbcType=INTEGER}


  and `type`=#{type,jdbcType=INTEGER}

mapper.insert:

insert into lesson (id, cid, title, url,
  price, source, `type`, comment_total_num,
  comment_rate_num, comment_good_num, comment_bad_num, 
  comment_medium_num, total_apply_num, recent_apply_num
  )
values (#{id,jdbcType=INTEGER}, #{cid,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR},
  #{price,jdbcType=DECIMAL}, #{source,jdbcType=VARCHAR}, #{cid,jdbcType=INTEGER}, #{commentTotalNum,jdbcType=INTEGER},
  #{commentRateNum,jdbcType=INTEGER}, #{commentGoodNum,jdbcType=INTEGER}, #{commentBadNum,jdbcType=INTEGER}, 
  #{commentMediumNum,jdbcType=INTEGER}, #{totalApplyNum,jdbcType=INTEGER}, #{recentApplyNum,jdbcType=INTEGER}
  )
